<?php

namespace App\RepairBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * RepairMessageRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RepairMessageRepository extends EntityRepository
{

    /**获取userId的消息
     * @param $userId
     * @return array|null
     */
    public function getMyMessage($userId)
    {
        $query = $this->getEntityManager()->createQueryBuilder('m')
            ->innerJoin('m.user','u')
            ->where('u.id = :userId')
            ->setParameters(array('userId'=>$userId))
            ->getQuery();

        try
        {
            return $query->getResult();
        }catch (\Doctrine\ORM\NoResultException $e){
            return null;
        }
    }

    /**获取userId未读的消息
     * @param $userId
     * @return mixed|null
     */
    public function countByUnread($userId)
    {
        $query = $this->getEntityManager()->createQueryBuilder('m')
            ->innerJoin('m.user','u')
            ->where('u.id = :userId and m.isRead = false')
            ->setParameters(array('userId'=>$userId))
            ->getQuery();

        try
        {
            return $query->getSingleScalarResult();
        }catch (\Doctrine\ORM\NoResultException $e){
            return null;
        }
    }


}
